import styled from "styled-components";

export const StyledButton = styled.button`
    height: 2rem;
    margin-top: 1rem;
    width: 5rem;
    border: 1px solid #d7d7d7;
    border-radius: 0.25rem;
    background: transparent;
    font-weight: 400;
    font-size: 1rem;
    display: flex;
    align-items: center;
    justify-content: center;
`
